q31_t arm_lut_q31(
  q31_t x,
  const q31_t *table)
{
    q31_t lutVal;                                  /* Temporary variables for input, output */
    int32_t index;                                 /* Index variables */
    q31_t a, b;                                    /* Four nearest output values */
    q31_t fract;                                   /* Temporary values for fractional values */

    /* Calculate the nearest index */
    index = (uint32_t)x >> FAST_MATH_Q31_SHIFT;

    /* Calculation of fractional value */
    fract = (x - (index << FAST_MATH_Q31_SHIFT)) << 9;

    /* Read two nearest values of input value from the look up table */
    a = table[index];
    b = table[index+1];

    /* Linear interpolation process */
    lutVal = (q63_t)(0x80000000-fract)*a >> 32;
    lutVal = (q31_t)((((q63_t)lutVal << 32) + ((q63_t)fract*b)) >> 32);

    return lutVal << 1;
}

float32_t lookup_u32(
  uint32_t x,
  const float32_t *table,
  uint32_t table_size)
{
  float32_t lutVal, weight;                      /* Temporary variables for input, output */
  int32_t index;                                 /* Index variables */
  float32_t a, b;                                /* Four nearest output values */
  uint32_t fract;                                /* Temporary values for fractional values */
  uint32_t pow, shift, mask;

  /* Linear interpolation process */
  const float32_t inv_scale = 2.3283064365386963e-10f;  // 1.0 / 2^32
  const uint32_t uint32max = 0xFFFFFFFF;

  /* Calculate the shift size */
  pow = 31 - __builtin_clz(table_size);  // Count Leading Zeros
  shift = 32 - pow;

  /* Calculate the nearest index */
  index = (uint32_t)x >> shift;

  /* Calculation of fractional value */
  mask = (uint32max >> shift);
  fract = (x & mask);

  /* Read two nearest values of input value from the look up table */
  a = table[index];
  b = table[index + 1];

  /* Linear interpolation process */
  weight = (float32_t)fract / (float32_t)(mask + 1);
  lutVal = a * (1.0f - weight) + b * weight;
  return lutVal;
}

/**
 * @brief  Fast approximation to the trigonometric sine function for floating-point data.
 * @param[in] x input value in radians.
 * @return  sin(x).
 */

float32_t arm_sin_f32(
  float32_t x)
{
  float32_t sinVal, fract, in;                           /* Temporary variables for input, output */
  uint16_t index;                                        /* Index variable */
  float32_t a, b;                                        /* Two nearest output values */
  int32_t n;
  float32_t findex;

  /* Special case for small negative inputs */
  if ((x < 0.0f) && (x >= -1.9e-7f)) {
     return x;
  }

  /* input x is in radians */
  /* Scale the input to [0 1] range from [0 2*PI] , divide input by 2*pi */
  in = x * 0.159154943092f;

  /* Calculation of floor value of input */
  n = (int32_t) in;

  /* Make negative values towards -infinity */
  if (x < 0.0f)
  {
    n--;
  }

  /* Map input value to [0 1] */
  in = in - (float32_t) n;

  /* Calculation of index of the table */
  findex = (float32_t) FAST_MATH_TABLE_SIZE * in;

  index = ((uint16_t)findex) & 0x1ff;

  /* fractional value calculation */
  fract = findex - (float32_t) index;

  /* Read two nearest values of input value from the sin table */
  a = sinTable_f32[index];
  b = sinTable_f32[index+1];

  /* Linear interpolation process */
  sinVal = (1.0f-fract)*a + fract*b;

  /* Return the output value */
  return (sinVal);
}
